ix = 'CHART_DATA:';\n        if (commentText.startsWith(prefix)) {\n            try {\n                const jsonString = commentText.substring(prefix.length).trim();\n                return JSON.parse(jsonString);\n            } catch (e) {\n                console.error(`Error parsing CHART_DATA for ${containerId}:`, e);\n                return null;\n            }\n        }\n    }\n    // console.warn(`No CHART_DATA comment found for container ${containerId}.`);\n    return null;\n}\n\n/**\n * Initializes and draws the Fear & Greed Gauge chart.\n * @param {'vi'|'en'} language - The desired language for labels.\n */\nfunction initializeFearGreedGauge_report(language) {\n    const lang = language;\n    const id = lang === 'en' ? 'fear-greed-gauge-container-en' : 'fear-greed-gauge-container';\n    const container = document.getElementById(id);\n    if (!container) return;\n\n    const chartData = getChartDataFromComment(id);\n    if (!chartData || chartData.type !== 'gauge') return;\n\n    const value = chartData.value;\n    const baseConfig = chartData.config;\n\n    // Vietnamese labels/config for segments\n    const segments_vi = [\n        {limit: 20, color: 'var(--fng-extreme-fear-color)', label: 'C·ª±c k·ª≥ S·ª£ h√£i'},\n        {limit: 40, color: 'var(--fng-fear-color)', label: 'S·ª£ h√£i'},\n        {limit: 60, color: 'var(--fng-neutral-color)', label: 'Trung t√≠nh'},\n        {limit: 80, color: 'var(--fng-greed-color)', label: 'Tham lam'},\n        {limit: 100, color: 'var(--fng-extreme-greed-color)', label: 'C·ª±c k·ª≥ Tham lam'}\n    ];\n\n    // English labels/config for segments\n    const segments_en = [\n        {limit: 20, color: 'var(--fng-extreme-fear-color)', label: 'Extreme Fear'},\n        {limit: 40, color: 'var(--fng-fear-color)', label: 'Fear'},\n        {limit: 60, color: 'var(--fng-neutral-color)', label: 'Neutral'},\n        {limit: 80, color: 'var(--fng-greed-color)', label: 'Greed'},\n        {limit: 100, color: 'var(--fng-extreme-greed-color)', label: 'Extreme Greed'}\n    ];\n\n    const currentSegments = lang === 'en' ? segments_en : segments_vi;\n\n    const config = {\n        min: baseConfig.min,\n        max: baseConfig.max,\n        segments: currentSegments\n    };\n\n    createGauge(container, value, config);\n}\n\n/**\n * Initializes and draws the RSI Gauge chart.\n * @param {'vi'|'en'} language - The desired language for labels.\n */\nfunction initializeRSIGauge_report(language) {\n    const lang = language;\n    const id = lang === 'en' ? 'rsi-gauge-container-en' : 'rsi-gauge-container';\n    const container = document.getElementById(id);\n    if (!container) return;\n\n    const chartData = getChartDataFromComment(id);\n    if (!chartData || chartData.type !== 'gauge') return;\n\n    const value = chartData.value;\n    const baseConfig = chartData.config;\n\n    // Vietnamese labels/config for segments\n    const segments_vi = [\n        {limit: 30, color: 'var(--rsi-oversold-color)', label: 'Qu√° b√°n'},\n        {limit: 70, color: 'var(--rsi-neutral-color)', label: 'Trung t√≠nh'},\n        {limit: 100, color: 'var(--rsi-overbought-color)', label: 'Qu√° mua'}\n    ];\n\n    // English labels/config for segments\n    const segments_en = [\n        {limit: 30, color: 'var(--rsi-oversold-color)', label: 'Oversold'},\n        {limit: 70, color: 'var(--rsi-neutral-color)', label: 'Neutral'},\n        {limit: 100, color: 'var(--rsi-overbought-color)', label: 'Overbought'}\n    ];\n\n    const currentSegments = lang === 'en' ? segments_en : segments_vi;\n\n    const config = {\n        min: baseConfig.min,\n        max: baseConfig.max,\n        segments: currentSegments\n    };\n\n    createGauge(container, value, config);\n}\n\n/**\n * Initializes and draws the BTC Dominance Doughnut chart.\n * @param {'vi'|'en'} language - The desired language for labels.\n */\nfunction initializeBTCDominance_report(language) {\n    const lang = language;\n    const id = lang === 'en' ? 'btc-dominance-doughnut-container-en' : 'btc-dominance-doughnut-container';\n    const container = document.getElementById(id);\n    if (!container) return;\n\n    const chartData = getChartDataFromComment(id);\n    if (!chartData || chartData.type !== 'doughnut') return;\n\n    const baseData = chartData.data;\n    const baseConfig = chartData.config;\n\n    // Translate labels for data points\n    const data_vi = baseData.map(item => ({\n        value: item.value,\n        color: item.color,\n        label: item.label === 'Bitcoin' ? 'Bitcoin' : 'Altcoins' // Assuming these are the only labels\n    }));\n\n    const data_en = baseData.map(item => ({\n        value: item.value,\n        color: item.color,\n        label: item.label === 'Bitcoin' ? 'Bitcoin' : 'Altcoins'\n    }));\n\n    const currentData = lang === 'en' ? data_en : data_vi;\n\n    // Config for Doughnut chart (title can be translated if needed, but BTC.D is usually universal)\n    const config_vi = {\n        title: 'BTC.D',\n        showLegend: baseConfig.showLegend,\n        outerRadius: baseConfig.outerRadius,\n        innerRadius: baseConfig.innerRadius\n    };\n\n    const config_en = {\n        title: 'BTC.D',\n        showLegend: baseConfig.showLegend,\n        outerRadius: baseConfig.outerRadius,\n        innerRadius: baseConfig.innerRadius\n    };\n\n    const cfg = lang === 'en' ? config_en : config_vi;\n    createDoughnutChart(container, currentData, cfg);\n}\n\n/**\n * Initializes and draws the Volume Bar chart.\n * @param {'vi'|'en'} language - The desired language for labels and options.\n */\nfunction initializeVolumeBar_report(language) {\n    const lang = language;\n    const id = lang === 'en' ? 'volume-bar-chart-container-en' : 'volume-bar-chart-container';\n    const container = document.getElementById(id);\n    if (!container) return;\n\n    const chartData = getChartDataFromComment(id);\n    if (!chartData || chartData.type !== 'bar') return;\n\n    const baseData = chartData.data;\n    const 