iousSibling;\n    while (currentNode && currentNode.nodeType !== Node.COMMENT_NODE) {\n        currentNode = currentNode.previousSibling;\n    }\n\n    if (currentNode && currentNode.nodeType === Node.COMMENT_NODE) {\n        const commentText = currentNode.nodeValue.trim();\n        const chartDataPrefix = "CHART_DATA: ";\n        if (commentText.startsWith(chartDataPrefix)) {\n            try {\n                const jsonString = commentText.substring(chartDataPrefix.length);\n                return JSON.parse(jsonString);\n            } catch (e) {\n                console.error(`Error parsing CHART_DATA for ${containerId}:`, e);\n                return null;\n            }\n        }\n    }\n    // console.warn(`No CHART_DATA comment found for container with ID '${containerId}'.`);\n    return null;\n}\n\n/**\n * Initializes the Fear & Greed Gauge chart.\n * @param {'vi'|'en'} language - The desired language for chart labels.\n */\nfunction initializeFearGreedGauge_report(language) {\n    const lang = language;\n    const id = lang === 'en' ? 'fear-greed-gauge-container-en' : 'fear-greed-gauge-container';\n    const container = document.getElementById(id);\n    if (!container) return;\n\n    const chartData = getChartDataFromComment(id);\n    if (!chartData || chartData.type !== 'gauge') return;\n\n    const value = chartData.value;\n\n    // Vietnamese labels/config\n    const config_vi = {\n        min: chartData.config.min,\n        max: chartData.config.max,\n        segments: [\n            {limit: 20, color: 'var(--fng-extreme-fear-color)', label: 'C·ª±c k·ª≥ S·ª£ h√£i'},\n            {limit: 40, color: 'var(--fng-fear-color)', label: 'S·ª£ h√£i'},\n            {limit: 59, color: 'var(--fng-neutral-color)', label: 'Trung t√≠nh'},\n            {limit: 79, color: 'var(--fng-greed-color)', label: 'Tham lam'},\n            {limit: 100, color: 'var(--fng-extreme-greed-color)', label: 'C·ª±c k·ª≥ Tham lam'}\n        ]\n    };\n\n    // English labels/config\n   