ntainer #conclusion-outlook-section .report-card ul li strong {\n    color: var(--text-accent); /* General emphasis for key points */\n}\n\n/* Scenario-specific color coding in conclusion */\n#report-container .scenario-bullish {\n    color: var(--positive-color);\n    font-weight: 600;\n}\n#report-container .scenario-neutral {\n    color: var(--neutral-color);\n    font-weight: 600;\n}\n#report-container .scenario-bearish {\n    color: var(--negative-color);\n    font-weight: 600;\n}\n\n/* Styling for the validation status */\n#report-container .status-pass {\n    color: var(--positive-color);\n    font-weight: bold;\n    text-transform: uppercase;\n    letter-spacing: 0.05em;\n}\n\n/* General styling for section header icons (colors are set inline in HTML) */\n#report-container h2 i {\n    margin-right: 0.5rem;\n}	// report.js\n\n/**\n * Helper function to extract JSON data from an HTML comment preceding a given container.\n * @param {string} containerId - The ID of the HTML container element.\n * @returns {object|null} The parsed JSON data, or null if not found/parsed.\n */\nfunction getChartDataFromComment_report(containerId) {\n    const container = document.getElementById(containerId);\n    if (!container) return null;\n\n    let prevSibling = container.previousSibling;\n    // Traverse backwards to find the first comment node, skipping text nodes (like whitespace)\n    while (prevSibling && prevSibling.nodeType !== Node.COMMENT_NODE) {\n        prevSibling = prevSibling.previousSibling;\n    }\n\n    if (prevSibling && prevSibling.nodeType === Node.COMMENT_NODE) {\n        const commentText = prevSibling.nodeValue.trim();\n        const dataPrefix = 'CHART_DATA: ';\n        if (commentText.startsWith(dataPrefix)) {\n            try {\n                const jsonString = commentText.substring(dataPrefix.length);\n                return JSON.parse(jsonString);\n            } catch (e) {\n                console.error(`Error parsing CHART_DATA for ${containerId}:`, e);\n                return null;\n            }\n        }\n    }\n    return null;\n}\n\n/**\n * Initializes and draws the Fear & Greed Gauge chart.\n * @param {'vi'|'en'} language - The language for chart labels and configuration.\n */\nfunction initializeFearGreedGauge_report(language) {\n    const lang = language;\n    const id = lang === 'en' ? 'fear-greed-gauge-container-en' : 'fear-greed-gauge-container';\n    const container = document.getElementById(id);\n    if (!container) return;\n\n    const chartData = getChartDataFromComment_report(id);\n    if (!chartData || chartData.type !== 'gauge') {\n        console.warn(`No valid gauge CHART_DATA found for ${id}`);\n        return;\n    }\n\n    const value = chartData.value;\n\n    // Vietnamese labels/config\n    const config_vi = {\n        min: chartData.config.min,\n        max: chartData.config.max,\n        segments: [\n            {limit: 20, color: 'var(--fng-extreme-fear-color)', label: 'C·ª±c k·ª≥ s·ª£ h√£i'},\n            {limit: 40, color: 'var(--fng-fear-color)', label: 'S·ª£ h√£i'},\n            {limit: 59, color: 'var(--fng-neutral-color)', label: 'Trung l·∫≠p'},\n            {limit: 79, color: 'var(--fng-greed-color)', label: 'Tham lam'},\n            {limit: 100, color: 'var(--fng-extreme-greed-color)', label: 'C·ª±c k·ª≥ tham lam'}\n        ]\n    };\n\n    // English labels/config\n    const config_en = {\n        min: chartData.config.min,\n        max: chartData.config.max,\n        segments: [\n            {limit: 20, color: 'var(--fng-extreme-fear-color)', label: 'Extreme Fear'},\n            {limit: 40, color: 'var(--fng-fear-color)', label: 'Fear'},\n            {limit: 59, color: 'var(--fng-neutral-color)', label: 'Neutral'},\n            {limit: 79, color: 'var(--fng-greed-color)', label: 'Greed'},\n            {limit: 100, color: 'var(--fng-extreme-greed-color)', label: 'Extreme Greed'}\n        ]\n    };\n\n    const cfg = lang === 'en' ? config_en : config_vi;\n    createGauge(container, value, cfg);\n}\n\n/**\n * Initializes and draws the Bitcoin Dominance Doughnut chart.\n * @param {'vi'|'en'} language - The language for chart labels and configuration.\n */\nfunction initializeBTCDominance_report(language) {\n    const lang = language;\n    const id = lang === 'en' ? 'btc-dominance-doughnut-container-en' : 'btc-dominance-doughnut-container';\n    const container = document.getElementById(id);\n    if (!container) return;\n\n    const chartData = getChartDataFromComment_report(id);\n    if (!chartData || chartData.type !== 'doughnut') {\n        console.warn(`No valid doughnut CHART_DATA found for ${id}`);\n        return;\n    }\n\n    // Data labels need to be translated if they are not generic (like "Bitcoin")\n    const data_vi = chartData.data.map(item => ({\n        value: item.value,\n        color: item.color,\n        label: item.label === 'Bitcoin' ? 'Bitcoin' : 'Altcoins'\n    }));\n    const data_en = chartData.data.map(item => ({\n        value: item.value,\n        color: item.color,\n        label: item.label === 'Bitcoin' ? 'Bitcoin' : 'Altcoins'\n    }));\n\n    // Config for both languages (title is the same 'BTC.D', legend is handled by data labels)\n    const config_common = {\n        title: chartData.config.title,\n        showLegend: chartData.config.showLegend,\n        outerRadius: chartData.config.outerRadius,\n        innerRadius: chartData.config.innerRadius\n    };\n\n    const data = lang === 'en' ? data_en : data_vi;\n    createDoughnutChart(container, data, config_common);\n}\n\n/**\n * Initializes and draws the Money Flow Index (MFI) Gauge chart.\n * @param {'vi'|'en'} language - The language for chart labels and configuration.\n */\nfunction initializeMFIGauge_report(language) {\n    const lang = language;\n    const id = lang === 'en' ? 'mfi-gauge-container-en' : 'mfi-gauge-container';\n    const container = document.getElementById(id);\n    if (!container) return;\n\n    const chartData = getChartDataFromComment_report(id);\n    if (!chartData || chartData.type !== 'gauge') {\n        console.warn(`No valid gauge CHART_DATA found for ${id}`);\n        return;\n    }\n\n    const value = chartData.value;\n\n    // Vietnamese labels/config\n    const config_vi = {\n        min: chartData.config.min,\n        max: chartData.config.max,\n        segments: [\n            {limit: 30, color: 'var(--rsi-oversold-color)', label: 'Qu√° b√°n'},\n            {limit: 70, color: 'var(--rsi-neutral-color)', label: 'Trung l·∫≠p'},\n            {limit: 100, color: 'var(--rsi-overbought-color)', label: 'Qu√° mua'}\n        ]\n    };\n\n    // English labels/config\n    const config_en = {\n        min: chartData.config.min,\n        max: chartData.config.max,\n        segments: [\n            {limit: 30, color: 'var(--rsi-oversold-color)', label: 'Oversold'},\n            {limit: 70, color: 'var(--rsi-neutral-color)', label: 'Neutral'},\n            {limit: 100, color: 'var(--rsi-overbought-color)', label: 'Overbought'}\n        ]\n    };\n\n    const cfg = lang === 'en' ? config_en : config_vi;\n    createGauge(container, value, cfg);\n}\n\n/**\n * Initializes and draws the Bitcoin Price Line chart.\n * @param {'vi'|'en'} language - The language for chart labels and configuration.\n */\nfunction initializeBTCPriceLine_report(language) {\n    const lang = language;\n    const id = lang === 'en' ? 'btc-price-line-chart-container-en' : 'btc-price-line-chart-container';\n    const container = document.getElementById(id);\n    if (!container) return;\n\n    const chartData = getChartDataFromComment_report(id);\n    if (!chartData || chartData.type !== 'line') {\n        console.warn(`No valid line CHART_DATA found for ${id}`);\n        return;\n    }\n\n    const data = chartData.data;\n\n    // Options are generally the same, valuePrefix/Suffix are usually universal symbols\n    const options = {\n        color: chartData.options.color,\n        valuePrefix: chartData.options.valuePrefix,\n        valueSuffix: chartData.options.valueSuffix\n    };\n\n    createLineChart(container, data, options);\n}\n\n/**\n * Initializes and draws the Volume Bar chart.\n * @param {'vi'|'en'} language - The language for chart labels and configuration.\n */\nfunction initializeVolumeBar_report(language) {\n    const lang = language;\n    const id = lang === 'en' ? 'volume-bar-chart-container-en' : 'volume-bar-chart-container';\n    const container = document.getElementById(id);\n    if (!container) return;\n\n    const chartData = getChartDataFromComment_report(id);\n    if (!chartData || chartData.type !== 'bar') {\n        console.warn(`No valid bar CHART_DATA found for ${id}`);\n        return;\n    }\n\n    // Data labels are coin symbols, so they don't need translation\n    const data = chartData.data.map(item => ({\n        value: item.value,\n        label: item.label,\n        color: item.color\n    }));\n\n    // Vietnamese options\n    const options_vi = {\n        valuePrefix: chartData.options.valuePrefix,\n        valueSuffix: chartData.options.valueSuffix,\n        yAxisLabel: 'Volume (T·ª∑ USD)'\n    };\n\n    // English options\n    const options_en = {\n        valuePrefix: chartData.options.valuePrefix,\n        valueSuffix: chartData.options.valueSuffix,\n        yAxisLabel: 'Volume (Billion USD)'\n    };\n\n    const options = lang === 'en' ? options_en : options_vi;\n    createBarChart(container, data, options);\n}\n\n\n/**\n * Main function to initialize all visual charts for the report.\n * It checks the language and passes it to individual chart initialization functions.\n * @param {'vi'|'en'} [language] - Optional language parameter. If not provided,\n *                                 it defaults to 'vi' or uses window.languageManager.currentLanguage.\n */\nfunction initializeAllVisuals_report(language = undefined) {\n    const lang = language || window.languageManager?.currentLanguage || 'vi';\n\n    // Initialize all charts, passing the determined language\n    initializeFearGreedGauge_report(lang);\n    initializeBTCDominance_report(lang);\n    initializeMFIGauge_report(lang);\n    initializeBTCPriceLine_report(lang);\n    initializeVolumeBar_report(lang);\n    // Add other chart initialization functions here as needed\n}	<section id="summary-section">\n    <h2><i class="fas fa-chart-line" style="color: var(--icon-color-summary);"></i> Executive Summary</h2>\n    <div class="report-card">\n        <p>The cryptocurrency market has experienced a significant correction in the past 24 hours, with Bitcoin's price dropping after reaching an all-time high, influenced by a higher-than-expected US Producer Price Index (PPI) report. This has dampened expectations for aggressive interest rate cuts by the Federal Reserve (Fed).</p>\n        <p>Despite this, market sentiment remains at <strong style="color: var(--fng-greed-color);">"Greed" (Fear & Greed Index: 60)</strong>. Strategic accumulation by institutions and whales continues, especially from companies like Strategy Inc. The regulatory landscape is becoming clearer with moves from the SEC and CFTC, promising to boost institutional participation.</p>\n        <p><strong>Key Highlights:</strong></p>\n        <ul>\n            <li>Bitcoin price drops after ATH due to higher-than-expected PPI.</li>\n            <li>Market sentiment remains "Greed" (F&G Index: 60).</li>\n            <li>Institutions and whales continue to accumulate BTC.</li>\n            <li>Regulatory landscape is becoming clearer, encouraging institutional participation.</li>\n            <li>Spot Bitcoin ETF inflows reach <strong style="color: var(--positive-color);">$14.8 billion</strong>.</li>\n        </ul>\n        <p><strong>Short-term Outlook:</strong> The market may undergo a consolidation phase. Key support levels for Bitcoin are $110,000 - $112,000. The possibility of a Fed rate cut in September is a potential catalyst.</p>\n    </div>\n</section>\n\n<section id="market-data