(--neutral-color);\n}\n#report-container #conclusion-outlook-section .report-card ul li:nth-child(3) strong { /* Bearish scenario */\n    color: var(--negative-color);\n}\n\n/* Validation section styling for the "PASS" result */\n#report-container #self-validation-section .validation-result {\n    background-color: var(--bull-card-bg); /* Use bull card background for pass */\n    border: 1px solid var(--positive-color); /* Green border for pass */\n    box-shadow: 0 4px 15px -3px rgba(22, 163, 74, 0.15); /* Subtle green shadow */\n    padding: 20px;\n    margin-top: 30px;\n    text-align: center;\n    border-radius: 8px;\n}\n#report-container #self-validation-section .validation-result h3 {\n    color: var(--positive-color);\n    font-size: 1.6em;\n    margin-bottom: 10px;\n    border-bottom: none; /* Remove border from h3 in this context */\n}\n#report-container #self-validation-section .validation-result p {\n    color: var(--text-secondary);\n    font-size: 1em;\n}\n\n/* Table note styling */\n#report-container .table-note {\n    font-size: 0.9em;\n    color: var(--text-secondary);\n    margin-top: 20px;\n    padding: 15px;\n    background-color: var(--bg-primary);\n    border-left: 5px solid var(--accent-color);\n    border-radius: 4px;\n}\n\n/* Chart containers - minimal styling for presentation, not chart content */\n#report-container .chart-container {\n    min-height: 200px; /* Ensure charts have a minimum height */\n    display: flex; /* Helps center chart content if applicable */\n    justify-content: center;\n    align-items: center;\n    margin-bottom: 20px;\n}	// report.js\n\n/**\n * Helper function to extract chart data from HTML comments.\n * It looks for a comment node immediately preceding the given container element\n * and parses the JSON string within 'CHART_DATA: {...}'.\n * @param {string} containerId - The ID of the HTML element that will contain the chart.\n * @returns {object|null} The parsed chart data object, or null if not found/invalid.\