h in the provided HTML)\n\n    // Vietnamese labels/config\n    const config_vi = {\n        min: baseConfig.min,\n        max: baseConfig.max,\n        segments: baseConfig.segments.map(s => {\n            let label_vi = s.label;\n            if (s.label === 'Extreme Fear') label_vi = 'C·ª±c k·ª≥ S·ª£ h√£i';\n            else if (s.label === 'Fear') label_vi = 'S·ª£ h√£i';\n            else if (s.label === 'Neutral') label_vi = 'Trung t√≠nh';\n            else if (s.label === 'Greed') label_vi = 'Tham lam';\n            else if (s.label === 'Extreme Greed') label_vi = 'C·ª±c k·ª≥ Tham lam';\n            return { ...s, label: label_vi };\n        })\n    };\n\n    // English labels/config (labels are already in English in the HTML example)\n    const config_en = {\n        min: baseConfig.min,\n        max: baseConfig.max,\n        segments: baseConfig.segments.map(s => ({ ...s, label: s.label }))\n    };\n\n    const cfg = lang === 'en' ? config_en : config_vi;\n    createGauge(container, value, cfg);\n}\n\n/**\n * Initializes the Bitcoin Dominance Doughnut chart.\n * @param {'vi'|'en'} language - The current language ('vi' for Vietnamese, 'en' for English).\n */\nfunction initializeBTCDominance_report(language) {\n    const lang = language;\n    const id = lang === 'en' ? 'btc-dominance-doughnut-container-en' : 'btc-dominance-doughnut-container';\n    const container = document.getElementById(id);\n    if (!container) return;\n\n    const chartData = getChartDataFromComment(id);\n    if (!chartData || chartData.type !== 'doughnut') {\n        console.error(`Invalid CHART_DATA or type for ${id}`);\n        return;\n    }\n\n    const baseData = chartData.data;\n    const baseConfig = chartData.config;\n\n    // Labels for Bitcoin and Altcoins are typically universal, so no specific translation needed here.\n    const data_vi = baseData.map(d => ({ ...d, label: d.label }));\n    const data_en = baseData.map(d => ({ ...d, label: d.label }));\n    const data = lang ==