ions_en : options_vi;\n\n    createLineChart(container, data, opts);\n}\n\n/**\n * Initializes the Ethereum Price Line chart.\n * @param {'vi'|'en'} language - The current language ('vi' for Vietnamese, 'en' for English).\n */\nfunction initializeETHPriceLine_report(language) {\n    const lang = language;\n    const id = lang === 'en' ? 'eth-price-line-chart-container-en' : 'eth-price-line-chart-container';\n    const container = document.getElementById(id);\n    if (!container) return;\n\n    const chartData = getChartDataFromComment(id);\n    if (!chartData || chartData.type !== 'line') {\n        console.error(`Invalid CHART_DATA or type for ${id}`);\n        return;\n    }\n\n    const data = chartData.data;\n    const baseOptions = chartData.options;\n\n    // Options for line charts (color, prefix, suffix) are generally universal.\n    const options_vi = { ...baseOptions };\n    const options_en = { ...baseOptions };\n    const opts = lang === 'en' ? options_en : options_vi;\n\n    createLineChart(container, data, opts);\n}\n\n/**\n * Main initialization function for all visuals in the report.\n * It detects the current language and initializes/redraws charts accordingly.\n * This function does NOT register event listeners for language changes;\n * it expects to be called by an external app layer when the language changes.\n * @param {'vi'|'en'} [language] - Optional. The language to use. If not provided,\n *                                 it defaults to `window.languageManager?.currentLanguage` or 'vi'.\n */\nfunction initializeAllVisuals_report(language = undefined) {\n    const lang = language || window.languageManager?.currentLanguage || 'vi';\n\n    // Initialize all charts, passing the determined language\n    initializeFearGreedGauge_report(lang);\n    initializeBTCDominance_report(lang);\n    initializeBitcoinRSIGauge_report(lang);\n    initializeEthereumRSIGauge_report(lang);\n    initializeMarketVolumeBar_report(lang);\n    initializeBTCPriceLine_re